export const openAIBackendEndpoint = asyncHandler(async (req, res) => {
    try {
        const { payload } = req.body;

        let parsedPayload = typeof payload === "string" ? JSON.parse(payload) : payload;
        if (!parsedPayload || !parsedPayload.messages)
            return handleError(res, 400, "en", "INVALID_PAYLOAD");

        // ðŸ”¹ Combine static and dynamic parts
        const messages = [...BASE_PROMPT, ...parsedPayload.messages];

        const openAIResponse = await openai.chat.completions.create({
            model: parsedPayload.model || "gpt-5",
            messages,
        });

        return handleSuccess(res, 200, "en", "OPENAI_RESPONSE", openAIResponse);
    } catch (error) {
        console.error("OpenAI Error:", error);
        return handleError(res, 500, "en", "OPENAI_ERROR", { error: error.message });
    }
});